    // --------------------------------------------------------------------------------
    // Function : properties()
    // Description :
    //     This method gives the properties of the archive.
    //     The properties are :
    //         nb : Number of files in the archive
    //         comment : Comment associated with the archive file
    //         status : not_exist, ok
    // Parameters :
    //     None
    // Return Values :
    //     0 on failure,
    //     An array with the archive properties.
    // --------------------------------------------------------------------------------
    /**
    * Get properties of the ZIP archive (comment, number of entries and status).
    * @example
    * $zip = new PclZip('my_archive.zip');
    * $result = $zip->properties();
    * print_r($result); // Array ( [comment] => 'Archive comment' [nb] => 3 [status] => 'ok' )
    * @param void $none - No arguments.
    * @returns array Return associative array with keys: 'comment' (string), 'nb' (int), 'status' (string, e.g. 'ok' or 'not_exist').
    */
    public function properties()
    {

        // ----- Reset the error handler
        $this->privErrorReset();

        // ----- Magic quotes trick
        $this->privDisableMagicQuotes();

        // ----- Check archive
        if (!$this->privCheckFormat()) {
            $this->privSwapBackMagicQuotes();
            return(0);
        }

        // ----- Default properties
        $v_prop = array();
        $v_prop['comment'] = '';
        $v_prop['nb'] = 0;
        $v_prop['status'] = 'not_exist';

        // ----- Look if file exists
        if (@is_file($this->zipname)) {
            // ----- Open the zip file
            if (($this->zip_fd = @fopen($this->zipname, 'rb')) == 0) {
                $this->privSwapBackMagicQuotes();

                // ----- Error log
                PclZip::privErrorLog(PCLZIP_ERR_READ_OPEN_FAIL, 'Unable to open archive \''.$this->zipname.'\' in binary read mode');

                // ----- Return
                return 0;
            }

            // ----- Read the central directory informations
            $v_central_dir = array();
            if (($v_result = $this->privReadEndCentralDir($v_central_dir)) != 1) {
                $this->privSwapBackMagicQuotes();
                return 0;
            }

            // ----- Close the zip file
            $this->privCloseFd();

            // ----- Set the user attributes
            $v_prop['comment'] = $v_central_dir['comment'];
            $v_prop['nb'] = $v_central_dir['entries'];
            $v_prop['status'] = 'ok';
        }

        // ----- Magic quotes trick
        $this->privSwapBackMagicQuotes();

        // ----- Return
        return $v_prop;
    }
