    // --------------------------------------------------------------------------------
    // Function : privList()
    // Description :
    // Parameters :
    // Return Values :
    // --------------------------------------------------------------------------------
    /**
    * Read the ZIP archive central directory and populate the provided list with file information entries.
    * @example
    * $list = array();
    * $zip = new PclZip('archive.zip');
    * $result = $zip->privList($list);
    * echo $result; // 1 on success, or an error code returned by PclZip::errorCode()
    * // Example $list after success:
    * // array(
    * //   0 => array('filename'=>'doc.txt','stored_filename'=>'doc.txt','index'=>0,'size'=>1024,'compressed_size'=>900,'mtime'=>1609459200, ...),
    * //   1 => array('filename'=>'image.png','stored_filename'=>'image.png','index'=>1,'size'=>20480,'compressed_size'=>12345,'mtime'=>1609459300, ...)
    * // );
    * @param {array} &$p_list - Reference to an array that will be filled with parsed file entries (metadata) from the archive.
    * @returns {int} Return 1 on success, or a PclZip error code on failure.
    */
    public function privList(&$p_list)
    {
        $v_result = 1;

        // ----- Magic quotes trick
        $this->privDisableMagicQuotes();

        // ----- Open the zip file
        if (($this->zip_fd = @fopen($this->zipname, 'rb')) == 0) {
            // ----- Magic quotes trick
            $this->privSwapBackMagicQuotes();

            // ----- Error log
            PclZip::privErrorLog(PCLZIP_ERR_READ_OPEN_FAIL, 'Unable to open archive \''.$this->zipname.'\' in binary read mode');

            // ----- Return
            return PclZip::errorCode();
        }

        // ----- Read the central directory informations
        $v_central_dir = array();
        if (($v_result = $this->privReadEndCentralDir($v_central_dir)) != 1) {
            $this->privSwapBackMagicQuotes();
            return $v_result;
        }

        // ----- Go to beginning of Central Dir
        @rewind($this->zip_fd);
        if (@fseek($this->zip_fd, $v_central_dir['offset'])) {
            $this->privSwapBackMagicQuotes();

            // ----- Error log
            PclZip::privErrorLog(PCLZIP_ERR_INVALID_ARCHIVE_ZIP, 'Invalid archive size');

            // ----- Return
            return PclZip::errorCode();
        }

        // ----- Read each entry
        for ($i=0; $i<$v_central_dir['entries']; $i++) {
            // ----- Read the file header
            if (($v_result = $this->privReadCentralFileHeader($v_header)) != 1) {
                $this->privSwapBackMagicQuotes();
                return $v_result;
            }
            $v_header['index'] = $i;

            // ----- Get the only interesting attributes
            $this->privConvertHeader2FileInfo($v_header, $p_list[$i]);
            unset($v_header);
        }

        // ----- Close the zip file
        $this->privCloseFd();

        // ----- Magic quotes trick
        $this->privSwapBackMagicQuotes();

        // ----- Return
        return $v_result;
    }
    // --------------------------------------------------------------------------------

    // --------------------------------------------------------------------------------
    // Function : privConvertHeader2FileInfo()
    // Description :
    //     This function takes the file informations from the central directory
    //     entries and extract the interesting parameters that will be given back.
    //     The resulting file infos are set in the array $p_info
    //         $p_info['filename'] : Filename with full path. Given by user (add),
    //                                                     extracted in the filesystem (extract).
    //         $p_info['stored_filename'] : Stored filename in the archive.
    //         $p_info['size'] = Size of the file.
    //         $p_info['compressed_size'] = Compressed size of the file.
    //         $p_info['mtime'] = Last modification date of the file.
    //         $p_info['comment'] = Comment associated with the file.
    //         $p_info['folder'] = true/false : indicates if the entry is a folder or not.
    //         $p_info['status'] = status of the action on the file.
    //         $p_info['crc'] = CRC of the file content.
    // Parameters :
    // Return Values :
    // --------------------------------------------------------------------------------
    /**
    * Convert a ZIP central directory header into a normalized file information array.
    * @example
    * $p_header = array(
    *     'filename' => 'dir/sub/file.txt',
    *     'stored_filename' => 'file.txt',
    *     'size' => 1234,
    *     'compressed_size' => 512,
    *     'mtime' => 1609459200,
    *     'comment' => 'Example file',
    *     'external' => 0x00000010,
    *     'index' => 3,
    *     'status' => 0,
    *     'crc' => 1234567890
    * );
    * $info = array();
    * $result = $this->privConvertHeader2FileInfo($p_header, $info);
    * print_r($info); // render processed file info array like ['filename'=>'dir/sub/file.txt','stored_filename'=>'file.txt','size'=>1234,'compressed_size'=>512,'mtime'=>1609459200,'comment'=>'Example file','folder'=>true,'index'=>3,'status'=>0,'crc'=>1234567890]
    * echo $result; // render 1
    * @param {array} $p_header - Header data (as read from the ZIP central directory) to convert.
    * @param {array} &$p_info - Reference to an array that will be filled with normalized file information.
    * @returns {int} Return 1 on success.
    */
    public function privConvertHeader2FileInfo($p_header, &$p_info)
    {
        $v_result=1;

        // ----- Get the interesting attributes
        $v_temp_path = PclZipUtilPathReduction($p_header['filename']);
        $p_info['filename'] = $v_temp_path;
        $v_temp_path = PclZipUtilPathReduction($p_header['stored_filename']);
        $p_info['stored_filename'] = $v_temp_path;
        $p_info['size'] = $p_header['size'];
        $p_info['compressed_size'] = $p_header['compressed_size'];
        $p_info['mtime'] = $p_header['mtime'];
        $p_info['comment'] = $p_header['comment'];
        $p_info['folder'] = (($p_header['external']&0x00000010)==0x00000010);
        $p_info['index'] = $p_header['index'];
        $p_info['status'] = $p_header['status'];
        $p_info['crc'] = $p_header['crc'];

        // ----- Return
        return $v_result;
    }
    // --------------------------------------------------------------------------------

    // --------------------------------------------------------------------------------
    // Function : privExtractByRule()
    // Description :
    //     Extract a file or directory depending of rules (by index, by name, ...)
    // Parameters :
    //     $p_file_list : An array where will be placed the properties of each
    //                                    extracted file
    //     $p_path : Path to add while writing the extracted files
    //     $p_remove_path : Path to remove (from the file memorized path) while writing the
    //                                        extracted files. If the path does not match the file path,
    //                                        the file is extracted with its memorized path.
    //                                        $p_remove_path does not apply to 'list' mode.
    //                                        $p_path and $p_remove_path are commulative.
    // Return Values :
    //     1 on success,0 or less on error (see error code list)
    // --------------------------------------------------------------------------------
    /**
    * Extract files from the ZIP archive according to provided rules/options and populate the given file list.
    * @example
    * $fileList = array();
    * $options = array(PCLZIP_OPT_BY_NAME => array('docs/', 'readme.txt'), PCLZIP_OPT_EXTRACT_AS_STRING => false);
    * $result = $pclzip->privExtractByRule($fileList, './archives/', 'docs/to/remove/', '', $options);
    * echo $result; // 1 on success or a PclZip error code on failure
    * @param {array} &$p_file_list - Reference to an array that will be filled with extracted file info entries (each entry contains attributes like stored_filename, status, content when applicable).
    * @param {string} $p_path - Destination base path where files will be extracted (relative or absolute). Example: './extracted'
    * @param {string} $p_remove_path - Path prefix to remove from stored filenames when extracting (should end with '/'). Example: 'folder/to/remove/'
    * @param {string} $p_remove_all_path - Path to remove entirely from stored filenames (if any). Example: ''
    * @param {array} &$p_options - Reference to an associative array of extraction options (e.g. PCLZIP_OPT_BY_NAME, PCLZIP_OPT_BY_PREG, PCLZIP_OPT_BY_INDEX, PCLZIP_OPT_EXTRACT_AS_STRING, PCLZIP_OPT_EXTRACT_IN_OUTPUT, PCLZIP_OPT_STOP_ON_ERROR).
    * @returns {int} Return 1 on success, or a PclZip error code (integer) on failure.
    */
    public function privExtractByRule(&$p_file_list, $p_path, $p_remove_path, $p_remove_all_path, &$p_options)
    {
        $v_result=1;

        // ----- Magic quotes trick
        $this->privDisableMagicQuotes();

        // ----- Check the path
        if (($p_path == "") || ((substr($p_path, 0, 1) != "/") && (substr($p_path, 0, 3) != "../") && (substr($p_path, 1, 2)!=":/"))) {
            $p_path = "./".$p_path;
        }

        // ----- Reduce the path last (and duplicated) '/'
        if (($p_path != "./") && ($p_path != "/")) {
            // ----- Look for the path end '/'
            while (substr($p_path, -1) == "/") {
                $p_path = substr($p_path, 0, strlen($p_path)-1);
            }
        }

        // ----- Look for path to remove format (should end by /)
        if (($p_remove_path != "") && (substr($p_remove_path, -1) != '/')) {
            $p_remove_path .= '/';
        }
        $p_remove_path_size = strlen($p_remove_path);

        // ----- Open the zip file
        if (($v_result = $this->privOpenFd('rb')) != 1) {
            $this->privSwapBackMagicQuotes();
            return $v_result;
        }

        // ----- Read the central directory informations
        $v_central_dir = array();
        if (($v_result = $this->privReadEndCentralDir($v_central_dir)) != 1) {
            // ----- Close the zip file
            $this->privCloseFd();
            $this->privSwapBackMagicQuotes();

            return $v_result;
        }

        // ----- Start at beginning of Central Dir
        $v_pos_entry = $v_central_dir['offset'];

        // ----- Read each entry
        $j_start = 0;
        for ($i=0, $v_nb_extracted=0; $i<$v_central_dir['entries']; $i++) {
            // ----- Read next Central dir entry
            @rewind($this->zip_fd);
            if (@fseek($this->zip_fd, $v_pos_entry)) {
                // ----- Close the zip file
                $this->privCloseFd();
                $this->privSwapBackMagicQuotes();

                // ----- Error log
                PclZip::privErrorLog(PCLZIP_ERR_INVALID_ARCHIVE_ZIP, 'Invalid archive size');

                // ----- Return
                return PclZip::errorCode();
            }

            // ----- Read the file header
            $v_header = array();
            if (($v_result = $this->privReadCentralFileHeader($v_header)) != 1) {
                // ----- Close the zip file
                $this->privCloseFd();
                $this->privSwapBackMagicQuotes();

                return $v_result;
            }

            // ----- Store the index
            $v_header['index'] = $i;

            // ----- Store the file position
            $v_pos_entry = ftell($this->zip_fd);

            // ----- Look for the specific extract rules
            $v_extract = false;

            // ----- Look for extract by name rule
            if ((isset($p_options[PCLZIP_OPT_BY_NAME])) && ($p_options[PCLZIP_OPT_BY_NAME] != 0)) {
                // ----- Look if the filename is in the list
                for ($j=0; ($j<sizeof($p_options[PCLZIP_OPT_BY_NAME])) && (!$v_extract); $j++) {
                    // ----- Look for a directory
                    if (substr($p_options[PCLZIP_OPT_BY_NAME][$j], -1) == "/") {
                        // ----- Look if the directory is in the filename path
                        if ((strlen($v_header['stored_filename']) > strlen($p_options[PCLZIP_OPT_BY_NAME][$j])) && (substr($v_header['stored_filename'], 0, strlen($p_options[PCLZIP_OPT_BY_NAME][$j])) == $p_options[PCLZIP_OPT_BY_NAME][$j])) {
                            $v_extract = true;
                        }
                    } elseif ($v_header['stored_filename'] == $p_options[PCLZIP_OPT_BY_NAME][$j]) {
                        // ----- Look for a filename
                        $v_extract = true;
                    }
                }
            } elseif ((isset($p_options[PCLZIP_OPT_BY_PREG])) && ($p_options[PCLZIP_OPT_BY_PREG] != "")) {
                // ----- Look for extract by preg rule
                if (preg_match($p_options[PCLZIP_OPT_BY_PREG], $v_header['stored_filename'])) {
                    $v_extract = true;
                }
            } elseif ((isset($p_options[PCLZIP_OPT_BY_INDEX])) && ($p_options[PCLZIP_OPT_BY_INDEX] != 0)) {
                // ----- Look for extract by index rule
                // ----- Look if the index is in the list
                for ($j=$j_start; ($j<sizeof($p_options[PCLZIP_OPT_BY_INDEX])) && (!$v_extract); $j++) {
                    if (($i>=$p_options[PCLZIP_OPT_BY_INDEX][$j]['start']) && ($i<=$p_options[PCLZIP_OPT_BY_INDEX][$j]['end'])) {
                            $v_extract = true;
                    }
                    if ($i>=$p_options[PCLZIP_OPT_BY_INDEX][$j]['end']) {
                            $j_start = $j+1;
                    }

                    if ($p_options[PCLZIP_OPT_BY_INDEX][$j]['start']>$i) {
                        break;
                    }
                }
            } else {
                // ----- Look for no rule, which means extract all the archive
                $v_extract = true;
            }

            // ----- Check compression method
            if (($v_extract) && (($v_header['compression'] != 8) && ($v_header['compression'] != 0))) {
                $v_header['status'] = 'unsupported_compression';

                // ----- Look for PCLZIP_OPT_STOP_ON_ERROR
                if ((isset($p_options[PCLZIP_OPT_STOP_ON_ERROR])) && ($p_options[PCLZIP_OPT_STOP_ON_ERROR] === true)) {
                    $this->privSwapBackMagicQuotes();

                    PclZip::privErrorLog(PCLZIP_ERR_UNSUPPORTED_COMPRESSION, "Filename '".$v_header['stored_filename']."' is compressed by an unsupported compression method (".$v_header['compression'].") ");

                    return PclZip::errorCode();
                }
            }

            // ----- Check encrypted files
            if (($v_extract) && (($v_header['flag'] & 1) == 1)) {
                $v_header['status'] = 'unsupported_encryption';
                // ----- Look for PCLZIP_OPT_STOP_ON_ERROR
                if ((isset($p_options[PCLZIP_OPT_STOP_ON_ERROR])) && ($p_options[PCLZIP_OPT_STOP_ON_ERROR] === true)) {
                    $this->privSwapBackMagicQuotes();

                    PclZip::privErrorLog(PCLZIP_ERR_UNSUPPORTED_ENCRYPTION, "Unsupported encryption for  filename '".$v_header['stored_filename']."'");

                    return PclZip::errorCode();
                }
            }

            // ----- Look for real extraction
            if (($v_extract) && ($v_header['status'] != 'ok')) {
                $v_result = $this->privConvertHeader2FileInfo($v_header, $p_file_list[$v_nb_extracted++]);
                if ($v_result != 1) {
                    $this->privCloseFd();
                    $this->privSwapBackMagicQuotes();
                    return $v_result;
                }

                $v_extract = false;
            }

            // ----- Look for real extraction
            if ($v_extract) {
                // ----- Go to the file position
                @rewind($this->zip_fd);
                if (@fseek($this->zip_fd, $v_header['offset'])) {
                    // ----- Close the zip file
                    $this->privCloseFd();

                    $this->privSwapBackMagicQuotes();

                    // ----- Error log
                    PclZip::privErrorLog(PCLZIP_ERR_INVALID_ARCHIVE_ZIP, 'Invalid archive size');

                    // ----- Return
                    return PclZip::errorCode();
                }

                // ----- Look for extraction as string
                if ($p_options[PCLZIP_OPT_EXTRACT_AS_STRING]) {
                    $v_string = '';

                    // ----- Extracting the file
                    $v_result1 = $this->privExtractFileAsString($v_header, $v_string, $p_options);
                    if ($v_result1 < 1) {
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();
                        return $v_result1;
                    }

                    // ----- Get the only interesting attributes
                    if (($v_result = $this->privConvertHeader2FileInfo($v_header, $p_file_list[$v_nb_extracted])) != 1) {
                        // ----- Close the zip file
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();

                        return $v_result;
                    }

                    // ----- Set the file content
                    $p_file_list[$v_nb_extracted]['content'] = $v_string;

                    // ----- Next extracted file
                    $v_nb_extracted++;

                    // ----- Look for user callback abort
                    if ($v_result1 == 2) {
                        break;
                    }
                } elseif ((isset($p_options[PCLZIP_OPT_EXTRACT_IN_OUTPUT])) && ($p_options[PCLZIP_OPT_EXTRACT_IN_OUTPUT])) {
                    // ----- Look for extraction in standard output
                    // ----- Extracting the file in standard output
                    $v_result1 = $this->privExtractFileInOutput($v_header, $p_options);
                    if ($v_result1 < 1) {
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();
                        return $v_result1;
                    }

                    // ----- Get the only interesting attributes
                    if (($v_result = $this->privConvertHeader2FileInfo($v_header, $p_file_list[$v_nb_extracted++])) != 1) {
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();
                        return $v_result;
                    }

                    // ----- Look for user callback abort
                    if ($v_result1 == 2) {
                        break;
                    }
                } else {
                    // ----- Look for normal extraction
                    // ----- Extracting the file
                    $v_result1 = $this->privExtractFile($v_header, $p_path, $p_remove_path, $p_remove_all_path, $p_options);
                    if ($v_result1 < 1) {
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();
                        return $v_result1;
                    }

                    // ----- Get the only interesting attributes
                    if (($v_result = $this->privConvertHeader2FileInfo($v_header, $p_file_list[$v_nb_extracted++])) != 1) {
                        // ----- Close the zip file
                        $this->privCloseFd();
                        $this->privSwapBackMagicQuotes();

                        return $v_result;
                    }

                    // ----- Look for user callback abort
                    if ($v_result1 == 2) {
                        break;
                    }
                }
            }
        }

        // ----- Close the zip file
        $this->privCloseFd();
        $this->privSwapBackMagicQuotes();

        // ----- Return
        return $v_result;
    }
