// --------------------------------------------------------------------------------
// Function : PclZipUtilPathReduction()
// Description :
// Parameters :
// Return Values :
// --------------------------------------------------------------------------------
/**
* Reduce/normalize a path string by resolving "." and ".." segments and removing redundant slashes.
* @example
* $result = PclZipUtilPathReduction('dir1/dir2/../file.txt');
* echo $result // "dir1/file.txt";
* @param {string} $p_dir - Path to reduce (may contain ".", "..", and redundant slashes).
* @returns {string} Reduced/normalized path string.
*/
function PclZipUtilPathReduction($p_dir)
{
    $v_result = "";

    // ----- Look for not empty path
    if ($p_dir != "") {
        // ----- Explode path by directory names
        $v_list = explode("/", $p_dir);

        // ----- Study directories from last to first
        $v_skip = 0;
        for ($i=sizeof($v_list)-1; $i>=0; $i--) {
            // ----- Look for current path
            if ($v_list[$i] == ".") {
                // ----- Ignore this directory
                // Should be the first $i=0, but no check is done
            } elseif ($v_list[$i] == "..") {
                $v_skip++;
            } elseif ($v_list[$i] == "") {
                // ----- First '/' i.e. root slash
                if ($i == 0) {
                    $v_result = "/".$v_result;
                    if ($v_skip > 0) {
                        // ----- It is an invalid path, so the path is not modified
                        // TBC
                        $v_result = $p_dir;
                        $v_skip = 0;
                    }
                } elseif ($i == (sizeof($v_list)-1)) {
                    // ----- Last '/' i.e. indicates a directory
                    $v_result = $v_list[$i];
                } else {
                    // ----- Double '/' inside the path
                    // ----- Ignore only the double '//' in path,
                    // but not the first and last '/'
                }
            } else {
                // ----- Look for item to skip
                if ($v_skip > 0) {
                    $v_skip--;
                } else {
                    $v_result = $v_list[$i].($i!=(sizeof($v_list)-1)?"/".$v_result:"");
                }
            }
        }

        // ----- Look for skip
        if ($v_skip > 0) {
            while ($v_skip > 0) {
                $v_result = '../'.$v_result;
                $v_skip--;
            }
        }
    }

    // ----- Return
    return $v_result;
}
